//import Frontend from "./Frontend";
import React from "react";
import './App.css';
class App extends React.Component {

  // Constructor 
  constructor(props) {
    super(props);

    this.state = {
      items: [],
      DataisLoaded: false
    };
  }

  // ComponentDidMount is used to
  // execute the code 
  componentDidMount() {
    fetch(
      "https://api.documenu.com/v2/restaurants/zip_code/11211?size=5"
)
      .then((res) => res.json())
      .then((json) => {
        this.setState({
          items: json,
          DataisLoaded: true
        });
      })
  }
  render() {
    const { DataisLoaded, items } = this.state;
    if (!DataisLoaded) return <div>
      <h1> Pleses wait some time.... </h1> </div>;

    return (
      <div className="App">
        <h1> Fetch data from an api in react </h1>  {
          items.map((item) => (
            <ol key={item.id} >
              restaurant_name: {item.name},
              cuisines: {item.cuisine},
              geo: {item.geo}
            </ol>
          ))
        }
      </div>
    );
  }
}

export default App;
